import React ,{useState}from 'react'
import Posts from  './Posts'
import Post from './Post'
import CreatePost from './CreatePost'
import{ Router,Link} from "@reach/router"
import { Menu } from 'antd';
import UpdatePost from './UpdatePost';
import SignIn from './SignIn'
import SignUp from './SignUp'
import { MailOutlined, AppstoreOutlined, SettingOutlined } from '@ant-design/icons';
import {auth} from '../firebase'
import MyPosts from './MyPosts'
import MyPostsSnippet from './MyPostsSnippet'
const {SubMenu} = Menu;
function App(props){
    const [user,setUser]=useState(false)
    auth.onAuthStateChanged(function(user) {
        if (user) {
          // User is signed in.
          // ...
          setUser(user)
          
        } else {
          // User is signed out.
          console.log("No user Signed in")
          //alert("Wrong password")
          // ...
        }
      });
      const onSignOut=()=>{
        console.log('signing out')
        auth.signOut().then(function() {

            console.log('user signed out')
            alert('sign out')
            setUser(false)
            // Sign-out successful.
          }).catch(function(error) {
            // An error happened.
          });
    }
      
    return (
        <div className="app_container">
            <div className="app_main_navigation">
        <Menu mode="horizontal">
        <Menu.Item key="posts" icon={<MailOutlined />}>
          <Link to="/posts" style={{ float:'right'}}>Posts</Link>
        </Menu.Item>
      
        {user && 
          <Menu.Item key="create_post"  icon={<AppstoreOutlined />}>
          <Link to="/create_post" style={{ float:'right'}}>Create Posts</Link>
          </Menu.Item>

    }
      {user &&
        <Menu.Item key="Myposts" icon={<SettingOutlined />}>
          <Link to={`/blogs/${user.uid}/posts`} style={{ float:'right'}}>My Posts</Link>
        </Menu.Item>
        }
            {!user 
              ?
              <Link to="/sign_in" style={{ float:'right'}}>Sign In</Link>
              :
              <a onClick ={onSignOut} style = {{float : 'right'}}>Sign Out</a>
            }
            
         </Menu>   

      

         </div>
            
            <Router>
                <SignUp path="sign_up"/>
                <SignIn path="sign_in" default/>
                <Posts path="posts" />
                <MyPosts path="blogs/:uid/posts" user={user}/>
                <CreatePost path="create_post" user={user}/>
                <Post path="post/:id"/>
                <UpdatePost path="update_post/:id"user={user}/>

            </Router>

        </div>
    )

}
export default App;
